import {makeAutoObservable} from 'mobx'

export default class UserStore {
    constructor() {
        this._isAuth = false
        this._user = {}
        this._accounts = []
        makeAutoObservable(this)
    }

    setIsAuth(bool) {
        this._isAuth = bool
    }
    setUser(user) {
        this._user = user
    }
    setAccounts(accounts) {
        this._accounts = accounts
    }

    get isAuth() {
        return this._isAuth
    }
    get user() {
        return this._user
    }
    get accounts() {
        return this._accounts
    }

    deleteUser(id) {
        this._accounts = this._accounts.filter(note => note._id !== id)
        this._accounts
    }

    toggleRole(id, role) {
        this._accounts = this.accounts.map(acc => acc._id === id 
            ? {
                ...acc,
                role: role
            }
            : {...acc})
    }
}
