import { observer } from 'mobx-react-lite';
import React, {useContext} from 'react';
import { ListGroup, Row, Col, ToggleButton, Button } from 'react-bootstrap';
import {Context} from '..'
import { updateRole } from '../http/userAPI';

const Account = observer( ({account}) => {

    const {user} = useContext(Context)
    const {alert} = useContext(Context)

    const click = async (val) => {
        account.role = val
        await updateRole(account).then(() => user.toggleRole(account._id, val))
    }

    const showModal = () => {
        alert.setModalVisible(true)
        alert.setModalNoteId(account._id)
    }

    // const onComplete = async () => {
    //     notka.isCompleted = !notka.isCompleted
    //     await updateNote(notka)
    // }

    return (
        <ListGroup.Item
            variant={account.role === 'ADMIN' ? 'danger' : 'success'}
        >
            <Row className='justify-content-between'>
                <Col>
                    {account.username}
                </Col>
                <Col>
                    {/* <ButtonGroup>
                        {radios.map((radio, idx) => (
                        <ToggleButton
                            key={idx}
                            id={`radio-${idx}`}
                            type="radio"
                            variant={idx % 2 ? 'success' : 'danger'}
                            name="radio"
                            value={radio.value}
                            checked={radioValue === radio.value}
                            onChange={(e) => setRadioValue(e.currentTarget.value)}
                        >
                            {radio.name}
                        </ToggleButton>
                        ))}
                    </ButtonGroup> */}
                </Col>
                <Col>
                    <ToggleButton
                        type="radio"
                        variant={account.role === 'USER' ? 'success' : 'outline-success'}
                        value={'USER'}
                        checked={account.role === 'USER'}
                        onClick={() => click('USER')}
                        className='me-1'
                    >
                        USER
                    </ToggleButton>
                    <ToggleButton
                        type="radio"
                        variant={account.role === 'ADMIN' ? 'danger' : 'outline-danger'}
                        value={'ADMIN'}
                        checked={account.role === 'ADMIN'}
                        onClick={() => click('ADMIN')}
                        className='me-2'
                    >
                        ADMIN
                    </ToggleButton>
                    <Button 
                        variant='dark'
                        onClick={() => showModal()}
                    >
                        Удалить пользователя
                    </Button>
                </Col>
            </Row>

        </ListGroup.Item>
    );
});

export default Account;
