                        <ToggleButton
                            type="radio"
                            variant={account.role === 'USER' ? 'success' : 'outline-success'}
                            value={'USER'}
                            checked={account.role === 'USER'}
                            onClick={() => click('USER')}
                        >
                            USER
                        </ToggleButton>
                        <ToggleButton
                            type="radio"
                            variant={account.role === 'ADMIN' ? 'danger' : 'outline-danger'}
                            value={'ADMIN'}
                            checked={account.role === 'ADMIN'}
                            onClick={() => click('ADMIN')}
                        >
                            ADMIN
                        </ToggleButton>
                </Col>
                <Col>
                    <Button variant='dark'>Удалить пользователя</Button>
                </Col>
            </Row>

        </ListGroup.Item>
    );
});

export default Account;
